import styles from './Signup.module.css';
import { useState } from 'react';
import backendApis from '../../utils/backendApis';
import { useNavigate } from 'react-router-dom';

function Signup() {
    const [signUpInfo, setSignUpInfo] = useState({
        userName: '',
        password: '',
        job: '',
        career: '',
    });
    const [error, setError] = useState('');

    const navigate = useNavigate();

    const signUpChangeHandler = (e) => {
        setSignUpInfo({ ...signUpInfo, [e.target.name]: e.target.value });
    };

    const signUpHandler = async (e) => {
        e.preventDefault();
        try {
            const res = await backendApis.signUp('POST', signUpInfo);
            if (res.message === 'User created') {
                alert('가입 성공! 로그인 해주세요 :)');
                setSignUpInfo({ userName: '', password: '', job: '', career: '' });
            } else {
                setError('가입 실패 : ' + (res.message || '알 수 없는 오류'));
                alert(error);
            }
        } catch (err) {
            setError('서버 연결 실패');
            alert(error);
        }
    };

    return (
        <div className={styles.container}>
            <h1 className={styles.h1}>.WORKLOG</h1>
            <h2 className={styles.h2}>SINGUP</h2>
            <form className={styles.signUp} onSubmit={signUpHandler}>
                <input
                    className={styles.input}
                    type="text"
                    placeholder="아이디"
                    name="userName"
                    value={signUpInfo.userName}
                    onChange={signUpChangeHandler}
                />
                <input
                    type="password"
                    placeholder="패스워드"
                    name="password"
                    value={signUpInfo.password}
                    onChange={signUpChangeHandler}
                />
                <div className={styles.box}>
                    <select name="job" value={signUpInfo.job} onChange={signUpChangeHandler}>
                        <option value="">직업 선택</option>
                        <option value="마케터">마케터</option>
                        <option value="개발자">개발자</option>
                        <option value="디자이너">디자이너</option>
                        <option value="기획자">기획자</option>
                        <option value="영업">영업</option>
                        <option value="기타">기타</option>
                        <option value="학생">학생</option>
                        <option value="백수">homeprotector</option>
                        <option value="next">NEXT</option>
                    </select>
                    <select name="career" value={signUpInfo.career} onChange={signUpChangeHandler}>
                        <option value="">경력 선택</option>
                        <option value="3년 이하 주니어">3년 이하 주니어</option>
                        <option value="3년 이상 시니어">3년 이상 시니어</option>
                    </select>
                </div>
                <button type="submit">Sign Up</button>
            </form>
        </div>
    );
}

export default Signup;
